import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ForgotPasswordPage.css';

function ForgotPasswordPage() {
  const [email, setEmail] = useState('');
  const [favoriteColor, setFavoriteColor] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const res = await fetch('/api/reset-password-security', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, favoriteColor, newPassword }),
      });
      const data = await res.json();
      setMessage(data.message || 'Password reset complete.');
    } catch {
      setMessage('Error resetting password.');
    }
  };

  const handleCancel = () => {
    navigate(-1); // Go back to previous page, hiding the forgot password page
  };

  return (
    <div className="forgot-password-container">
      <h2>Reset Password</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            required
            onChange={e => setEmail(e.target.value)}
          />
        </label>
        <label>
          Security Question: What is your favorite color?
          <input
            type="text"
            value={favoriteColor}
            required
            onChange={e => setFavoriteColor(e.target.value)}
          />
        </label>
        <label>
          New Password:
          <input
            type="password"
            value={newPassword}
            required
            minLength={6}
            onChange={e => setNewPassword(e.target.value)}
          />
        </label>
        <button type="submit">Reset Password</button>
        <button type="button" onClick={handleCancel} style={{ marginLeft: '10px' }}>
          Cancel
        </button>
      </form>
      {message && <p style={{ marginTop: 15 }}>{message}</p>}
    </div>
  );
}

export default ForgotPasswordPage;
