import React, { useEffect, useState } from 'react';
import './Home.css';

export default function Home() {
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState('');
  const [refreshKey, setRefreshKey] = useState(0);

  useEffect(() => {
    fetch('/api/summary')
      .then(res => res.json())
      .then(data => setSummary(data))
      .catch(() => setError('Failed to load summary.'));
  }, [refreshKey]); // Refetch when refreshKey changes

  // Listen for custom events when transactions are added
  useEffect(() => {
    const handleTransactionAdded = () => {
      setRefreshKey(prev => prev + 1);
    };

    window.addEventListener('transactionAdded', handleTransactionAdded);
    return () => window.removeEventListener('transactionAdded', handleTransactionAdded);
  }, []);

  if (error) return <div className="home-root">{error}</div>;
  if (!summary) return <div className="home-root">Loading...</div>;

  return (
    <div className="home-root">
      <h1>Finance Summary</h1>
      <p><strong>Total Income:</strong> ${summary.totalIncome}</p>
      <p><strong>Total Expenses:</strong> ${summary.totalExpenses}</p>
      <p><strong>Balance:</strong> ${summary.balance}</p>
      <h2>Expenses by Category</h2>
      <ul>
        {summary.categories.map(cat => (
          <li key={cat.name}>
            {cat.name}: ${cat.amount}
          </li>
        ))}
      </ul>
      <h2>Recent Transactions</h2>
      <ul>
        {summary.recent.map(tx => (
          <li key={tx.id}>
            [{tx.date}] {tx.type} - {tx.category}: ${tx.amount}
          </li>
        ))}
      </ul>
    </div>
  );
}
